import React, { useState } from "react";
// import { useMutation } from "@apollo/react-hooks";
import { Mutation } from "react-apollo";
import { gql } from "apollo-boost";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e, tokenAuth, client) => {
    e.preventDefault();
    const res = await tokenAuth({ variables: { username, password } });
    localStorage.setItem("authToken", res.data.tokenAuth.token);
    client.cache.writeData({ data: { isLoggedIn: true } });
  };

  return (
    <Mutation mutation={TOKEN_AUTH_MUTATION} variables={{ username, password }}>
      {(tokenAuth, { loading, error, client }) => (
        <form onSubmit={e => handleSubmit(e, tokenAuth, client)}>
          <input
            type="text"
            placeholder="username"
            onChange={e => setUsername(e.target.value)}
            value={username}
          />
          <input
            type="password"
            placeholder="password"
            onChange={e => setPassword(e.target.value)}
            value={password}
          />
          <button type="submit">Submit</button>
        </form>
      )}
    </Mutation>
  );
};

const TOKEN_AUTH_MUTATION = gql`
  mutation($username: String!, $password: String!) {
    tokenAuth(username: $username, password: $password) {
      token
    }
  }
`;

export default Login;
